targets.query:
from(bucket: "${Bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "DevicePerformance")

  |> filter(fn: (r) => r["_field"] == "Designed Capacity" or r["_field"] == "Full Charged Capacity")

  |> last()
  |> distinct(column: "_value")
  |> group(columns: ["_time"])
  |> pivot(rowKey:["_measurement"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => {
      batteryPercentage = if exists r["Full Charged Capacity"] and exists r["Designed Capacity"] and r["Designed Capacity"] != 0.0 then (float(v: r["Full Charged Capacity"]) / float(v: r["Designed Capacity"])) * 100.0 else 0.0
      return {
          _measurement: r._measurement,
          "Battery Capacity (%)": batteryPercentage
      }
  })
